import React, { useState } from "react";
import { Link } from "react-router-dom";
import Hamburger from "./Hamburger";

const Header = () => {
  const [state, setState] = useState({
    initial: true,
    open: false,
    menuName: "Menu",
  });

  const handleClick = () => {
    if (state.initial === true) {
      console.log("1");
      setState({
        initial: false,
        open: true,
        menuName: "Close",
      });
    } else if (state.open === true) {
      console.log("2");
      setState({
        initial: false,
        open: false,
        menuName: "Menu",
      });
    } else if (state.open === false) {
      console.log("3");
      setState({
        initial: false,
        open: true,
        menuName: "Close",
      });
    }
  };

  return (
    <header>
      <div className="container">
        <div className="wrapper">
          <div className="inner-header">
            <div className="logo">
              <Link to="/">HAMBRG.</Link>
            </div>
            <div className="menu">
              <button onClick={() => handleClick()}>Menu</button>
            </div>
          </div>
        </div>
      </div>
      <Hamburger />
    </header>
  );
};

export default Header;
