import React, { useState, useEffect } from "react";
import { Link, withRouter } from "react-router-dom";
import Hamburger from "./Hamburger";

const Header = ({ history }) => {
  const [state, setState] = useState({
    initial: true,
    open: false,
    menuName: "Menu",
  });

  const [disabled, setDisabled] = useState(false);

  useEffect(() => {
    history.listen(() => {
      handleDisabled();
      setState({
        initial: false,
        open: false,
        menuName: "Menu",
      });
    });
  });

  const handleClick = () => {
    handleDisabled();
    if (state.initial === true) {
      setState({
        initial: false,
        open: true,
        menuName: "Close",
      });
    } else if (state.open === true) {
      setState({
        initial: false,
        open: false,
        menuName: "Menu",
      });
    } else if (state.open === false) {
      setState({
        initial: false,
        open: true,
        menuName: "Close",
      });
    }
  };

  const handleDisabled = () => {
    setDisabled(true);
    setTimeout(() => {
      setDisabled(false);
    }, 1000);
  };

  return (
    <header>
      <div className="container">
        <div className="wrapper">
          <div className="inner-header">
            <div className="logo">
              <Link to="/">HAMBRG.</Link>
            </div>
            <div className="menu">
              <button disabled={disabled} onClick={() => handleClick()}>
                Menu
              </button>
            </div>
          </div>
        </div>
      </div>
      <Hamburger state={state} />
    </header>
  );
};

export default withRouter(Header);
