import React, { useEffect, useRef } from "react";
import { Link } from "react-router-dom";

import { gsap } from "gsap";

import Dallas from "../images/dallas.webp";
import Austin from "../images/austin.webp";
import NewYork from "../images/newyork.webp";
import SanFranscisco from "../images/sanfrancisco.webp";
import Beijing from "../images/beijing.webp";

let cities = [
  { name: "Dallas", image: Dallas },
  { name: "Austin", image: Austin },
  { name: "New York", image: NewYork },
  { name: "San Franscisco", image: SanFranscisco },
  { name: "Beijing", image: Beijing },
];

const Hamburger = ({ state }) => {
  let menu = useRef(null);
  let reMenu = useRef(null);
  let mainBack = useRef(null);
  let cityBack = useRef(null);
  let line1 = useRef(null);
  let line2 = useRef(null);
  let line3 = useRef(null);
  let info = useRef(null);

  useEffect(() => {
    if (state.open === false && state.initial === false) {
      gsap.to([reMenu, mainBack], {
        duration: 0.8,
        height: 0,
        ease: "power3.inOut",
        stagger: {
          amount: 0.1,
        },
      });
      gsap.to(menu, {
        duration: 1,
        css: { display: "none" },
      });
    } else if (state.open === true && state.initial === false) {
      gsap.to(menu, {
        duration: 0,
        css: { display: "block" },
      });

      gsap.to([mainBack, reMenu], {
        duration: 0,
        height: "100%",
        opacity: 1,
        ease: "power3.inOut",
      });
      gsap.from([mainBack, reMenu], {
        duration: 0.8,
        height: 0,
        transformOrigin: "right top",
        skewY: 2,
        ease: "power3.inOut",
        stagger: {
          amount: 0.1,
        },
      });
      gsap.from(info, {
        duration: 1,
        y: 60,
        opacity: 0,
        delay: 0.2,
        ease: "power3.inOut",
      });

      gsap.from([line1, line2, line3], {
        duration: 0.8,
        y: 100,
        delay: 0.1,
        transformOrigin: "left top",
        skewY: 10,
        ease: "power3.inOut",
        stagger: {
          amount: 0.3,
        },
      });
    }
  }, [state]);

  let setCityBack = (image) => {
    gsap.to(cityBack, {
      duration: 0,
      background: `url(${image}) center center`,
    });

    gsap.to(cityBack, {
      duration: 0.5,
      opacity: 1,
      ease: "power2.inOut",
    });
  };

  let removeCityBack = (image) => {
    gsap.to(cityBack, {
      duration: 0.4,
      opacity: 0,
    });
  };

  let setlinkHover = (e) => {
    gsap.to(e.target, {
      duration: 0.4,
      skewX: 4,
      y: 3,
    });
  };

  let removelinkHover = (e) => {
    gsap.to(e.target, {
      duration: 0.4,
      skewX: 0,
      y: 0,
    });
  };

  return (
    <div ref={(el) => (menu = el)} className="hamburger-menu">
      <div
        ref={(el) => (mainBack = el)}
        className="menu-secondry-background-color"
      ></div>
      <div ref={(el) => (reMenu = el)} className="menu-layer">
        <div
          ref={(el) => (cityBack = el)}
          className="menu-city-background"
        ></div>
        <div className="container">
          <div className="wrapper">
            <div className="menu-links">
              <nav>
                <ul>
                  <li>
                    <Link
                      onMouseEnter={(e) => setlinkHover(e)}
                      onMouseLeave={(e) => removelinkHover(e)}
                      ref={(el) => (line1 = el)}
                      to="/opportunities"
                    >
                      Opprtunities
                    </Link>
                  </li>
                  <li>
                    <Link
                      onMouseEnter={(e) => setlinkHover(e)}
                      onMouseLeave={(e) => removelinkHover(e)}
                      ref={(el) => (line2 = el)}
                      to="/solutions"
                    >
                      Solution
                    </Link>
                  </li>
                  <li>
                    <Link
                      onMouseEnter={(e) => setlinkHover(e)}
                      onMouseLeave={(e) => removelinkHover(e)}
                      ref={(el) => (line3 = el)}
                      to="/contact-us"
                    >
                      {" "}
                      Contact us
                    </Link>
                  </li>
                </ul>
              </nav>
              <div ref={(el) => (info = el)} className="info">
                <h3>Our Promise</h3>
                <p>
                  Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                  Repellat ex odio debitis animi illo deleniti ut
                  necessitatibus, dolore aperiam. Nobis suscipit aut quibusdam
                  neque officia. Ducimus eos velit atque reiciendis!
                </p>
              </div>
              <div className="locations">
                Locations:
                {cities.map((item) => (
                  <span
                    onMouseEnter={() => setCityBack(item.image)}
                    onMouseLeave={() => removeCityBack(item.image)}
                  >
                    {item.name}
                  </span>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Hamburger;
