import React, { useEffect, useRef } from "react";
import { Link } from "react-router-dom";

const Hamburger = ({ state }) => {
  let menu = useRef(null);
  let reMenu = useRef(null);
  let mainBack = useRef(null);
  let cityBack = useRef(null);
  let line1 = useRef(null);
  let line2 = useRef(null);
  let line3 = useRef(null);
  let info = useRef(null);

  useEffect(() => {
    if (state.open === false) {
      menu.style.display = "none";
    } else if (state.open === true && state.initial === false) {
      menu.style.display = "block";
    } else if (state.open === true) {
      menu.style.display = "block";
    }
  });

  return (
    <div ref={(el) => (menu = el)} className="hamburger-menu">
      <div
        ref={(el) => (mainBack = el)}
        className="menu-secondry-background-color"
      ></div>
      <div ref={(el) => (reMenu = el)} className="menu-layer">
        <div
          ref={(el) => (cityBack = el)}
          className="menu-city-background"
        ></div>
        <div className="container">
          <div className="wrapper">
            <div className="menu-links">
              <nav>
                <ul>
                  <li>
                    <Link ref={(el) => (line1 = el)} to="/opportunities">
                      Opprtunities
                    </Link>
                  </li>
                  <li>
                    <Link ref={(el) => (line2 = el)} to="/solutions">
                      Solution
                    </Link>
                  </li>
                  <li>
                    <Link ref={(el) => (line3 = el)} to="/contact-us">
                      {" "}
                      Contact us
                    </Link>
                  </li>
                </ul>
              </nav>
              <div ref={(el) => (info = el)} className="info">
                <h3>Our Promise</h3>
                <p>
                  Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                  Repellat ex odio debitis animi illo deleniti ut
                  necessitatibus, dolore aperiam. Nobis suscipit aut quibusdam
                  neque officia. Ducimus eos velit atque reiciendis!
                </p>
              </div>
              <div className="locations">
                Locations:
                <span>Dallas</span>
                <span>Austin</span>
                <span>New York</span>
                <span>San Franscisco</span>
                <span>Beijing</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Hamburger;
